mapper.select:
select
id, type, sub_type, number, create_time, oper_time, sup_id, pdt_id, clt_id, remark, delete_flag
from nbmt.nbmt_order


        and id = #{id}


        and type = #{type}


        and sub_type = #{subType}


        and number = #{number}


        and create_time = #{createTime}


        and oper_time = #{operTime}


        and sup_id = #{supId}


        and pdt_id = #{pdtId}


        and clt_id = #{cltId}


        and remark = #{remark}


        and delete_flag = #{deleteFlag}
